import React from 'react';
import './TableCreation.css'; // Import the CSS file for styling

const TableCreation = ({ groupedItems }) => {
  return (
    <div>
      <h1>Monday.com Board Tables</h1>
      {Object.keys(groupedItems).length === 0 ? (
        <p>Loading data...</p>
      ) : (
        <div>
          {Object.keys(groupedItems).map((groupId) => {
            const group = groupedItems[groupId];
            return (
              <div key={groupId}>
                <h2>Group: {group.title}</h2>
                <table className="custom-table">
                  <thead>
                    <tr>
                      <th>Item Name</th>
                      {group.items[0].column_values.map((col) => (
                        <th key={col.id}>{col.title}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {group.items.map((item) => (
                      <tr key={item.id}>
                        <td>{item.name}</td>
                        {item.column_values.map((col) => (
                          <td key={col.id}>
                            {col.value && typeof col.value === 'string'
                              ? JSON.parse(col.value).date ||
                                JSON.parse(col.value).resource_costing
                              : col.value}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default TableCreation;
