export async function createItem(boardId, groupId, columnValues) {
    // Construct the mutation query for item creation
    const columnValuesInput = Object.entries(columnValues).map(([key, value]) => ({
      column_id: key,
      value,
    }));
  
    const query = `
      mutation createNewItem($boardId: Int!, $groupId: String!, $columnValues: [ColumnValueInput]!) {
        create_item(
          board_id: $boardId,
          group_id: $groupId,
          column_values: $columnValues
        ) {
          id
        }
      }
    `;
  
    // Define variables for the query
    const variables = {
      boardId,
      groupId,
      columnValues: columnValuesInput,
    };
  
    // Make the mutation API call
    const response = await fetch("https://api.monday.com/v2", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk", // Replace with your API key
      },
      body: JSON.stringify({
        query,
        variables,
      }),
    });
  
    if (!response.ok) {
      throw new Error("Failed to create item");
    }
  
    const data = await response.json();
  
    if (data.errors) {
      // Handle errors if the mutation fails
      throw new Error(data.errors[0].message);
    }
  
    return data.data.create_item;
  }
