import React from 'react';
import { Calendar } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css'; // Import the calendar styles
import moment from 'moment';

const MyCalendar = ({ events }) => {
  return (
    <Calendar
      events={events}
      startAccessor="start"
      endAccessor="end"
      views={['month', 'week', 'day']}
      eventStyleGetter={(event, start, end, isSelected) => {
        return {
          style: {
            backgroundColor: event.color, // Set the background color from the event object
            color: 'white', // You can set text color or other styles if needed
          },
        };
      }}
    />
  );
};

export default MyCalendar;
