// CalendarEvents.js
import moment from 'moment';

const extractCalendarEvents = (groupedItems) => {
  // Extract events from groupedItems and format them for the calendar
  const events = [];

  for (const groupId in groupedItems) {
    const group = groupedItems[groupId];
    group.items.forEach((item) => {
      // Extract relevant data and format for events
      const dateValue = item.column_values.find(col => col.title === 'Date').value;
      const hoursSpentValue = item.column_values.find(col => col.title === 'Hours Spent').value;

      if (dateValue && hoursSpentValue) {
        const event = {
          start: moment(dateValue.date).startOf('day').toDate(), // Use only the date part
          end: moment(dateValue.date).add(1, 'hour').toDate(),
          title: `${item.name} - ${hoursSpentValue} hours`, // Include Hours Spent in the title
          // ... other event properties
        };
        events.push(event);
      }
    });
  }

  return events;
};

export default extractCalendarEvents;
