import React, { Component } from 'react';
import styles from './Form.css';
import { fetchColumns } from './getids';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [], // Initialize columns as an empty array
      name: '',
      email: '',
    };
  }

  async componentDidMount() {
    try {
      const columns = await fetchColumns();
      this.setState({ columns });
    } catch (error) {
      console.error('Error Fetching data.', error);
    }
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to a server
    console.log(this.state);
  }

  render() {
    const { columns } = this.state; // Get columns from the component's state
    console.log(columns);
    const formContainerStyle = {
      display: 'flex',
      flexDirection: 'column', // Align fields vertically
      alignItems: 'center', // Center-align fields horizontally
    };

    return (
      <div style={formContainerStyle}>
        <form onSubmit={this.handleSubmit}>
          {/* Render input fields based on columns */}
          {columns.map(column => (
            <div key={column.id}>
              <label>{column.title}</label>
              {column.type === 'color' ? (
                <select
                  name={column.title}
                  value={this.state[column.title]}
                  onChange={this.handleInputChange}
                >
                  {/* You can render options for the dropdown here */}
                  <option value="option1">Option 1</option>
                  <option value="option2">Option 2</option>
                  <option value="option3">Option 3</option>
                </select>
              ) : (
                <input
                  type={column.type}
                  name={column.title}
                  value={this.state[column.title]}
                  onChange={this.handleInputChange}
                />
              )}
            </div>
          ))}
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default Form;
