// mutation.js

export async function createItem(boardId, groupId, columnValues) {
  // Construct the mutation query to create an item
  const query = `
    mutation {
      create_item (
        board_id: ${boardId},
        group_id: "${groupId}",
        column_values: ${JSON.stringify(columnValues).replace(/"([^"]+)":/g, '$1:')}
      ) {
        id
      }
    }
  `;

  const response = await fetch("https://api.monday.com/v2", {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'YOUR_API_KEY' // Replace with your Monday.com API key
    },
    body: JSON.stringify({
      'query': query
    })
  });

  if (!response.ok) {
    throw new Error('Failed to create item');
  }

  const data = await response.json();
  const responseData = data.data;
  return responseData.create_item;
}

export default createItem;
