import React, { useState, useEffect } from 'react';
import './App.css';
import Form from './Form';
import ApiCall from './getValues';
import MyCalendar from './MyCalendar';
import extractCalendarEvents from './CalendarEvents';

const App = () => {
  const [selectedData, setSelectedData] = useState(null);
  const [calendarEvents, setCalendarEvents] = useState([]);

  const populateForm = (selectedData) => {
    setSelectedData(selectedData);
  };

  useEffect(() => {
    // Fetch and update calendar events when the component mounts
    ApiCall({
      render: (groupedItems) => {
        const events = extractCalendarEvents(groupedItems);
        setCalendarEvents(events);
      },
    });
  }, []);

  return (
    <div className="App">
      <div>
        <Form title="Your Form Title" populateForm={populateForm} selectedData={selectedData} />
      </div>
      <div className="calendar-container">
        <MyCalendar events={calendarEvents} />
      </div>
      <div>
        <ApiCall
          render={(groupedItems, workspace, boardName) => {
            const events = extractCalendarEvents(groupedItems);
            setCalendarEvents(events);
          }}
        />
      </div>
    </div>
  );
};

export default App;
