// Example LoginButton component
import React from 'react';

const LoginButton = () => {
  const handleLogin = () => {
    // Redirect the user to Monday.com OAuth authorization URL
    window.location.href = `https://auth.monday.com/oauth2/authorize?client_id=dfd788a13315955c3ef1bc3fb2914b85&response_type=token`;
  };

  return (
    <button onClick={handleLogin}>Login with Monday.com</button>
  );
};

export default LoginButton;
