import React, { Component } from 'react';
import './Form.css';
import { fetchColumns } from './getids';
import { createItem } from './mutation';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [],
      ...props, // Initialize with props
    };
  }

  async componentDidMount() {
    try {
      const columns = await fetchColumns();
      // Initialize state values to null for each column
      const initialValues = {};
      columns.forEach((column) => {
        initialValues[column.title] = null;
      });
      this.setState({ columns, ...initialValues });
    } catch (error) {
      console.error('Error Fetching data.', error);
    }
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newItem = await createItem(
        this.state.boardId, // Replace with the actual boardId
        this.state.groupId, // Replace with the actual groupId
        this.state,
        this.state.columns // Pass the columns array
      );
      console.log("Created item:", newItem);
    } catch (error) {
      console.error('Error creating item:', error);
    }
  }
  

  render() {
    const { columns } = this.state;

    return (
      <div className="form-container">
        <h1>{}</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            {columns.map((column, index) => (
              <div key={column.id} className="form-input">
                <label>{column.title}</label>
                {column.type === 'color' ? (
                  <select
                    name={column.title}
                    value={this.state[column.title] || ''} // Provide a default empty string
                    onChange={this.handleInputChange}
                  >
                    {/* Add your options for the select element */}
                  </select>
                ) : (
                  <input
                    type={column.type === 'color' ? 'text' : column.type}
                    name={column.title}
                    value={this.state[column.title] || ''} // Provide a default empty string
                    onChange={this.handleInputChange}
                  />
                )}
              </div>
            ))}
          </div>
          <div className="form-submit">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Form;
