import React, { useState } from 'react';
import './App.css';
//import mondaySdk from 'monday-sdk-js';
import 'monday-ui-react-core/dist/main.css';
import Form from './Form';
import ApiCall from './getValues';
import TableCreation from './TableCreation';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const events = [
  {
    start: moment().toDate(),
    end: moment().add(0, "days").toDate(),
    title: "Some title"
  },
  // ... other events
];


const App = () => {
  const [selectedData, setSelectedData] = useState(null);

  // Function to update the selected data
  const populateForm = (selectedData) => {
    setSelectedData(selectedData);
  };

  const dayPropGetter = date => {
    return {
      className: 'day-cell', // Apply the class for date cells
    };
  };


  return (
    <div className="App">
      <div>
        <Form title="Your Form Title" populateForm={populateForm} />
      </div>
      <div className="calendar-container">
        <Calendar
          defaultDate={moment().toDate()}
          localizer={localizer}
          defaultView="month"
          events={events}
          //resizable
          //style={{ height: "100vh" }}
          //dayPropGetter={dayPropGetter} // Apply styles to date cells
        />
      </div>
      {/* <div>
        <ApiCall
          render={(groupedItems, workspace, boardName) => (
            <TableCreation groupedItems={groupedItems} boardName={boardName} workspace={workspace} populateForm={populateForm} />
          )}
        />
      </div> */}
    </div>
  );
};

export default App;
