import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const MyCalendar = ({ events }) => {
  return (
    <Calendar
      localizer={localizer}
      events={events}
      startAccessor="start"
      endAccessor="end"
      defaultView="month"
      views={['month', 'week', 'day', 'agenda']}
      allDayAccessor="isAllDay"
      components={{
        event: CustomEvent, // Use the CustomEvent component for styling events
        agenda: {
          event: CustomAgendaEvent, // Use the CustomAgendaEvent component for styling agenda events
        },
      }}
      style={{ height: 500 }} // Set the desired height for the calendar
    />
  );
};

// Custom Event component for styling individual events
const CustomEvent = ({ event }) => (
  <div style={{ backgroundColor: event.color, borderRadius: '5px', padding: '5px' }}>
    {event.title}
  </div>
);

// Custom Agenda Event component for styling agenda events
const CustomAgendaEvent = ({ event }) => (
  <div style={{ backgroundColor: event.color, borderRadius: '5px', padding: '5px' }}>
    <strong>{event.title}</strong>
    <div>
      {event.isAllDay ? 'All day' : `${moment(event.start).format('LT')} - ${moment(event.end).format('LT')}`}
    </div>
  </div>
);

export default MyCalendar;
