import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import extractCalendarEvents from './CalendarEvents'; // Import the function

const localizer = momentLocalizer(moment);

const MyCalendar = ({ events }) => {
  const CustomAgenda = ({ events, date }) => {
    return (
      <div>
        <h3>Spent Hour - {moment(date).format('MMMM D, YYYY')}</h3>
        <ul>
          {events.map((event) => (
            <li key={event.id}>{event.title}</li>
          ))}
        </ul>
      </div>
    );
  };

  const CustomToolbar = () => {
    return (
      <div>
        <h3>Spent Hour</h3>
      </div>
    );
  };

  const CustomWeekView = ({ date, ...props }) => {
    // Customize the week view to hide the time column
    return <Calendar.TimeGrid {...props} />;
  };

  return (
    <Calendar
      localizer={localizer}
      events={events}
      startAccessor="start"
      endAccessor="end"
      defaultView="week"
      views={{
        week: CustomWeekView,
      }}
      components={{
        agenda: {
          body: CustomAgenda,
        },
        //toolbar: CustomToolbar,
      }}
      allDayAccessor="isAllDay" // Specify the allDayAccessor property
    />
  );
};

export default MyCalendar;
