import React, { Component } from 'react';
import './Form.css';
import { fetchColumns, fetchBoardAndGroupId } from './getids';
import { createItem } from './mutation';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [],
      boardId: null, // Initialize boardId as null
      groupId: null, // Initialize groupId as null
      successMessage: '', // Add this
      errorMessage: '', // Add this
      ...props,
    };
  }

  async componentDidMount() {
    try {
      const columns = await fetchColumns();
      const { boardId, groupId } = await fetchBoardAndGroupId(); // Fetch boardId and groupId
      console.log(columns);
      const initialValues = {};
      columns.forEach((column) => {
        initialValues[column.title] = null;
      });

      this.setState({ columns, boardId, groupId, ...initialValues });
    } catch (error) {
      console.error('Error Fetching data.', error);
    }
  }
  handleInputChange = (e, column) => {
    const { value } = e.target;
    this.setState((prevState) => ({
      ...prevState,
      [column.id]: value,
    }));
  }

  resetFormState() {
    const { columns } = this.state;
    const initialValues = {};
    columns.forEach((column) => {
      initialValues[column.title] = null;
    });

    this.setState({ successMessage: '', errorMessage: '', ...initialValues });
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const { boardId, groupId, columns, ...values } = this.state;
  
    // Filter out columns with values
    const columnValues = {};
    columns.forEach((column) => {
      const columnId = column.id;
      if (values[columnId]) {
        columnValues[columnId] = values[columnId];
      }
    });
  
    try {
      const newItem = await createItem(boardId, groupId, columnValues);
  
      if (newItem) {
        this.setState({
          successMessage: 'Item created successfully',
          errorMessage: '',
        });
      } else {
        this.setState({ errorMessage: 'Error creating item', successMessage: '' });
      }
    } catch (error) {
      console.error('Error creating item:', error);
      this.setState({ errorMessage: 'Error creating item', successMessage: '' });
    }
    this.resetFormState();
  }
  

  render() {
    const { columns, successMessage, errorMessage } = this.state;

    return (
      <div className="form-container">
        <h1>{}</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            {columns.map((column, index) => (
              <div key={column.id} className="form-input">
                <label>{column.title}</label>
                {column.type === 'color' ? (
                  <select
                    name={column.title}
                    value={this.state[column.id] || ''}
                    onChange={(e) => this.handleInputChange(e, column)}
                  >
                    {column.settings_str ? (
                    Object.keys(JSON.parse(column.settings_str).labels).map((labelKey) => (
                      <option
                        key={labelKey}
                        value={labelKey}
                      >
                        {JSON.parse(column.settings_str).labels[labelKey]}
                      </option>
                    ))
                  ) : null}
                  </select>
                ) : (
                  <input
                    type={column.type === 'color' ? 'text' : column.type}
                    name={column.title}
                    value={this.state[column.id] || ''}
                    onChange={(e) => this.handleInputChange(e, column)}
                  />
                )}
              </div>
            ))}
          </div>
          <div className="form-submit">
            <button type="submit">Submit</button>
          </div>
        </form>
        {successMessage && <div className="success-message">{successMessage}</div>}
        {errorMessage && <div className="error-message">{errorMessage}</div>}
      </div>
    );
  }
}

export default Form;
