// mutation.js

export async function createItem(boardId, groupId, itemData) {
  // Construct the mutation query to create an item
  const query = `
  mutation {
    create_item (
      board_id: ${boardId},
      group_id: "${groupId}",
      item_name: "${itemData.name}", // Replace with the appropriate field name
      column_values: {
        // Add column values here based on your board setup
        // For example:
        "text_column": "${itemData.text_column}",
        "status_column": {"label": "${itemData.status_column}"}
      }
    ) {
      id
    }
  }
`;

  const response = await fetch("https://api.monday.com/v2", {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'YOUR_API_KEY' // Replace with your Monday.com API key
    },
    body: JSON.stringify({
      'query': query
    })
  });

  if (!response.ok) {
    throw new Error('Failed to create item');
  }

  const data = await response.json();
  const responseData = data.data;
  return responseData.create_item;
}

export default createItem;
