import React from 'react';
import './TableCreation.css'; // Import the CSS file for styling

const TableCreation = ({ groupedItems }) => {
    const extractValue = (col) => {
        if (!col.value) return ''; // Handle null or undefined values
      
        try {
          const parsedValue = JSON.parse(col.value);
          if (parsedValue) {
            if (col.type === 'duration') {
              const startDate = parsedValue.startDate || '';
              const endDate = parsedValue.endDate || '';
            //   if (startDate && endDate) {
            //     const startTime = new Date(parsedValue.started_at);
            //     const endTime = new Date(parsedValue.ended_at);
            //     const startDateTime = new Date(startDate);
            //     const endDateTime = new Date(endDate);
      
            //     if (startDateTime.toDateString() === endDateTime.toDateString()) {
            //       // If the start and end dates are the same, calculate hours
            //       const hours = ((endTime - startTime) / (1000 * 60 * 60)).toFixed(2);
            //       return `${hours} hours`;
            //     } else {
            //       // If the dates are different, show both dates
            //       return `${startDateTime.toLocaleDateString()} - ${endDateTime.toLocaleDateString()}`;
            //     }
            //   } else {
            //     return startDate || endDate;
            //   }
            } else if (col.type === 'date') {
              return parsedValue.date || '';
            } else if (col.type === 'numeric') {
              return parsedValue || '';
            } else if (col.type === 'color') {
              return parsedValue.index !== undefined ? parsedValue.index : '';
            } else if (col.type === 'multiple-person') {
              return Array.isArray(parsedValue.personsAndTeams) && parsedValue.personsAndTeams.length
                ? parsedValue.personsAndTeams[0].id
                : '';
            } else if (col.type === 'board-relation') {
              const linkedPulseIds = parsedValue.linkedPulseIds;
              if (Array.isArray(linkedPulseIds) && linkedPulseIds.length > 0) {
                return linkedPulseIds[0].linkedPulseId;
              } else {
                return '';
              }
            }
          }
        } catch (error) {
          console.error('JSON parsing error:', error);
          return col.value; // Return the original string value if parsing fails
        }
      
        return col.value; // Return the value as is for other data types
      };
      

  return (
    <div>
      <h1>Monday.com Board Tables</h1>
      {Object.keys(groupedItems).length === 0 ? (
        <p>Loading data...</p>
      ) : (
        <div>
          {Object.keys(groupedItems).map((groupId) => {
            const group = groupedItems[groupId];
            return (
              <div key={groupId}>
                <h2>Group: {group.title}</h2>
                <table className="custom-table">
                  <thead>
                    <tr>
                      <th>Item Name</th>
                      {group.items[0].column_values.map((col) => (
                        <th key={col.id}>{col.title}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {group.items.map((item) => (
                      <tr key={item.id}>
                        <td>{item.name}</td>
                        {item.column_values.map((col) => (
                          <td key={col.id}>{extractValue(col)}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default TableCreation;
