import React, { Component } from 'react';
import styles from './Form.css';


class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
    };
 }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to a server
    console.log(this.state);
  }

  render() {
    return (
      <div>
        <form className={styles.form} onSubmit={this.handleSubmit}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleInputChange}
              className={styles.input} // Use the CSS class for the input field
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleInputChange}
              className={styles.input} // Use the CSS class for the input field
            />
            </div>
          <div>
            <button type="submit" className={styles.button}>Submit</button> {/* Use the CSS class for the button */}
          </div>
        </form>
      </div>
    );
  }
}

export default Form;
