import React, { Component } from 'react';
import styles from './Form.css';


let query = '{boards(ids: 5398461564) {columns {id title}}}';
const apiResponse = {};

fetch ("https://api.monday.com/v2", {
  method: 'post',
  headers: {
    'Content-Type': 'application/json',
    'Authorization' : 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk'
   },
   body: JSON.stringify({
     'query' : query
   })
  })
   .then(res => res.json())
   .then(res => console.log(JSON.stringify(res, null, 2)))
   .then(res => apiResponse);  

   const columnTitles = apiResponse.data.boards[0].columns;

   // Create initial formData object with empty values
   const initialFormData = {};
   columnTitles.forEach(column => {
     initialFormData[column.id] = '';
   });
 
   this.setState({ formData: initialFormData });

class DynamicForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {},
    };
 }

  // handleInputChange = (e) => {
  //   this.setState({ [e.target.name]: e.target.value });
  // }

  // handleSubmit = (e) => {
  //   e.preventDefault();
  //   // Handle form submission, e.g., send data to a server
  //   console.log(this.state);
  // }


handleInputChange = (e) => {
  const { name, value } = e.target;
  this.setState((prevState) => ({
    formData: {
      ...prevState.formData,
      [name]: value,
    },
  }));
}

handleSubmit = (e) => {
  e.preventDefault();
  console.log(this.state.formData);
}

render() {
  const { formData } = this.state;
  const columnTitles = Object.keys(formData);

  return (
    <div>
      <form onSubmit={this.handleSubmit}>
        {columnTitles.map((columnId) => (
          <div key={columnId}>
            <label>{formData[columnId]}:</label>
            <input
              type="text"
              name={columnId}
              value={formData[columnId]}
              onChange={this.handleInputChange}
            />
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
}
  

export default DynamicForm;
