import React, { Component } from 'react';

class ApiCall extends Component {
  constructor(props) {
    super(props);
    this.state = {
      groupedItems: {},
    };
  }

  componentDidMount() {
    // Perform the API call to fetch data from Monday.com
    fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk', 
      },
      body: JSON.stringify({
        'query': '{boards (ids:5382001836) {name workspace {id name} items{id name group{id title} column_values{id title value type}}}}', 
      })
    })
      .then((res) => res.json())
      .then((data) => {
        const boards = data.data.boards;
        const groupedItems = {};
        const workspace = boards[0].workspace;
        const boardName = boards[0].name;
        boards.forEach((board) => {
          board.items.forEach((item) => {
            const groupId = item.group.id;
            if (!groupedItems[groupId]) {
              groupedItems[groupId] = {
                title: item.group.title,
                items: [],
              };
            }
            groupedItems[groupId].items.push(item);
          });
        });

        this.setState({ groupedItems, workspace, boardName });
        console.log(groupedItems, workspace);
      })
      .catch((error) => {
        console.error('Error fetching data from Monday.com:', error);
      });
  }

  render() {
    return this.props.render(this.state.groupedItems, this.state.workspace, this.setState.boardName);
  }
}

export default ApiCall;
