import React, { Component } from 'react';
import './Form.css';
import { fetchColumns } from './getids';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [], // Initialize columns as an empty array
      name: '',
      email: '',
    };
  }

  async componentDidMount() {
    try {
      const columns = await fetchColumns();
      this.setState({ columns });
    } catch (error) {
      console.error('Error Fetching data.', error);
    }
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to a server
    console.log(this.state);
  }

  render() {
    const { columns } = this.state; // Get columns from the component's state
    console.log(columns);
    return (
      <div className="form-outer-container">
        <div className="form-container">
          <form onSubmit={this.handleSubmit}>
            {columns.map(column => (
              <div key={column.id} className="form-input">
                <div className="form-field">
                  <label>{column.title}</label>
                  {column.type === 'color' ? (
                    <select
                      name={column.title}
                      value={this.state[column.title]}
                      onChange={this.handleInputChange}
                    >
                      {/* Options for the dropdown */}
                    </select>
                  ) : (
                    <input
                      type={column.type}
                      name={column.title}
                      value={this.state[column.title]}
                      onChange={this.handleInputChange}
                    />
                  )}
                </div>
              </div>
            ))}
            <div className="form-submit">
              <button type="submit">Submit</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default Form;
