export async function createItem(boardId, groupId, itemData, columnTypes) {
  // Filter out columns with null values
  const filteredItemData = Object.entries(itemData)
    .filter(([key, value]) => value !== null && value !== undefined)
    .reduce((obj, [key, value]) => ({ ...obj, [key]: value }), {});

  // Construct the mutation query dynamically
  const columnValues = Object.entries(filteredItemData)
    .map(([key, value]) => {
      if (columnTypes[key] === 'board-relation' && typeof value === 'object' && value !== null) {
        return `\\\"${key}\\\":{\\\"linkedPulseId\\\":\\\"${value.selectedItemId}\\\"}`;
      } else {
        return `\\\"${key}\\\": \\\"${value}\\\"`;
      }
    })
    .join(', ');

  const query = `mutation {
    create_item (
      board_id: ${boardId},
      group_id: "${groupId}",
      item_name: "${filteredItemData.name}",
      column_values: "{${columnValues}}"
    ) {
      id
    }
  }`;

  // Make the API request with the dynamic query
  const response = await fetch("https://api.monday.com/v2", {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk', // Replace with your API key
    },
    body: JSON.stringify({
      'query': query,
    }),
  });

  if (!response.ok) {
    const responseData = await response.json();
    console.log(responseData);
    throw new Error('Failed to create item');
  }

  const data = await response.json();
  return data.data;
}
