export async function createItem(boardId, groupId, values, columns) {
    // Construct the mutation query for item creation
    const columnValues = Object.keys(values).map((columnTitle) => ({
      column_id: getColumnId(columns, columnTitle),
      value: values[columnTitle],
    }));
  
    const query = `
      mutation {
        create_item(
          board_id: ${boardId},
          group_id: "${groupId}",
          column_values: ${JSON.stringify(columnValues)}
        ) {
          id
        }
      }
    `;
  
    // Make the mutation API call
    const response = await fetch("https://api.monday.com/v2", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk' // Replace with your API key
      },
      body: JSON.stringify({
        query
      })
    });
  
    if (!response.ok) {
      throw new Error("Failed to create item");
    }
  
    const data = await response.json();
    return data.data.create_item;
}

// Add a function to get the column_id based on column title
function getColumnId(columns, columnTitle) {
    const column = columns.find((col) => col.title === columnTitle);
    return column ? column.id : null;
  }
