export async function createItem(boardId, groupId, itemData) {
  // Construct the mutation query dynamically
  const columnValues = Object.keys(itemData)
    .map((columnId) => `"${columnId}": ${JSON.stringify(itemData[columnId])}`)
    .join(', ');

  const query = `mutation {
    create_item (
      board_id: ${boardId},
      group_id: "${groupId}",
      item_name: "${itemData.name}",
      column_values: {${columnValues}}
    ) {
      id
    }
  }`;

  // Make the API request with the dynamic query
  const response = await fetch("https://api.monday.com/v2", {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'YOUR_API_KEY', // Replace with your API key
    },
    body: JSON.stringify({
      'query': query,
    }),
  });

  if (!response.ok) {
    const responseData = await response.json();
    console.log(responseData);
    throw new Error('Failed to create item');
  }

  const data = await response.json();
  return data.data;
}
