import React, { Component } from 'react';
import './Form.css';

import { fetchColumns } from './getids';
import { createItem } from './mutation'; // You need to import the createItem function from your mutation file.

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [],
      selectedData: null,
      ...props,
    };
  }

  async componentDidMount() {
    try {
      const columns = await fetchColumns();
      const initialValues = {};
      columns.forEach((column) => {
        initialValues[column.title] = null;
      });
      this.setState({ columns, ...initialValues });
    } catch (error) {
      console.error('Error Fetching data.', error);
    }
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const { columns, boardId, groupId, ...values } = this.state;

    // Filter out columns with null or undefined values
    const itemData = {};
    columns.forEach((column) => {
      const value = values[column.title];
      if (value !== null && value !== undefined) {
        itemData[column.title] = value;
      }
    });

    try {
      const newItem = await createItem(boardId, groupId, itemData);
      console.log("Created item:", newItem);
    } catch (error) {
      console.error('Error creating item:', error);
    }
  }

  // Function to populate the form fields with selected data
  populateForm = (selectedData) => {
    this.setState({ selectedData });
  };

  render() {
    const { columns, selectedData } = this.state;
    const { title } = this.props;

    return (
      <div className="form-container">
        <h1>{title}</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            {columns.map((column, index) => (
              <div key={column.id} className="form-input">
                <label>{column.title}</label>
                {column.type === 'color' ? (
                  <select
                    name={column.title}
                    value={selectedData ? selectedData[column.title] : ''}
                    onChange={this.handleInputChange}
                  >
                    <option value="option1">Option 1</option>
                    <option value="option2">Option 2</option>
                    {/* Add more options as needed */}
                  </select>
                ) : (
                  <input
                    type={column.type} // Fix the type attribute
                    name={column.title}
                    value={selectedData ? selectedData[column.title] : ''}
                    onChange={this.handleInputChange}
                  />
                )}
              </div>
            ))}
          </div>
          <div className="form-submit">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Form;
