export async function createItem(boardId, groupId, columnValues) {
    // Construct the mutation query for item creation
    const columnValuesInput = columnValues.map(({ column_id, value }) => ({
      column_id,
      value
    }));
  
    const query = `
      mutation {
        create_item(
          board_id: ${boardId},
          group_id: "${groupId}",
          item_name: "New Item Name",
          column_values: ${JSON.stringify(columnValuesInput)}
        ) {
          id
        }
      }
    `;
  
    // Make the mutation API call
    const response = await fetch("https://api.monday.com/v2", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "YOUR_API_KEY_HERE"
      },
      body: JSON.stringify({
        query
      })
    });
  
    if (!response.ok) {
      throw new Error("Failed to create item");
    }
  
    const data = await response.json();
  
    if (data.errors) {
      throw new Error("Error creating item: " + JSON.stringify(data.errors));
    }
  
    return data.data.create_item;
  }
