let columnsData = null; // Variable to store the fetched columns

export async function fetchColumns() {
  if (columnsData === null) {
    // Fetch columns only if they haven't been fetched yet
    const query = '{boards(ids: 5382001836) {columns {id title type settings_str}}}';

    const response = await fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Your-API-Key' // Replace with your API key
      },
      body: JSON.stringify({
        'query': query
      })
    });

    if (!response.ok) {
      throw new Error('Failed to fetch data');
    }

    const data = await response.json();
    const responseData = data.data;
    const boards = responseData.boards;

    if (boards.length > 0) {
      columnsData = boards[0].columns;
    } else {
      columnsData = [];
    }
  }

  return columnsData;
}
