import React, { Component } from 'react';
import styles from './Form.css';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [], // Initialize columns as an empty array
      name: '',
      email: '',
    };
  }

  componentDidMount() {
    let query = '{boards(ids: 5382001836) {columns {id title}}}';

    fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjI5MTk3MzkwMCwiYWFpIjoxMSwidWlkIjo1MDc0MDc5NiwiaWFkIjoiMjAyMy0xMC0yNlQxMTozOToxNy4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTg2ODA3NDQsInJnbiI6InVzZTEifQ.YqNGkoV6ioF5pgYl_F9t32cOSUxaX_ETL_iPmpEjBOk' // Replace with your API key
      },
      body: JSON.stringify({
        'query': query
      })
    })
      .then(res => res.json())
      .then(data => {
        const responseData = data.data;
        const boards = responseData.boards;

        if (boards.length > 0) {
          const columns = boards[0].columns;
          this.setState({ columns }); // Set the columns in the component's state
        } else {
          console.log('No data available');
        }
      })
      .catch(error => {
        console.error('Error Fetching data.', error);
      });
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to a server
    console.log(this.state);
  }

  render() {
    const { columns } = this.state; // Get columns from the component's state
    console.log(columns);
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          {/* Render input fields based on columns */}
          {columns.map(column => (
            <div key={column.id}>
              <label>{column.title}</label>
              <input
                type="text"
                name={column.title} // Use the column title as the input name
                value={this.state[column.title]} // Bind input value to state
                onChange={this.handleInputChange}
              />
            </div>
          ))}
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default Form;
