import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import extractCalendarEvents from './CalendarEvents'; // Import the function

const localizer = momentLocalizer(moment);

const MyCalendar = ({ events }) => {

const [selectedEvent, setSelectedEvent] = useState(null);
  const CustomAgenda = ({ events, date }) => {
    return (
      <div>
        <h3>Spent Hour - {moment(date).format('MMMM D, YYYY')}</h3>
        <ul>
          {events.map((event) => (
            <li key={event.id}>{event.title}</li>
          ))}
        </ul>
      </div>
    );
  };

  const CustomWeekView = ({ date, ...props }) => {
    // Customize the week view to hide the time column
    return <Calendar.TimeGrid {...props} />;
  };

  const CustomEventPopup = ({ event }) => {
    const handleAllDayChange = (e) => {
      const updatedEvent = { ...event, allDay: e.target.checked };
      setSelectedEvent(updatedEvent);
    };

    return (
      <div>
        <h3>{event.title}</h3>
        <div>
          <label>
            All Day:
            <input
              type="checkbox"
              checked={event.allDay || false}
              onChange={handleAllDayChange}
            />
          </label>
        </div>
        {/* Include other event details as needed */}
      </div>
    );
  };

  const handleEventClick = (event) => {
    setSelectedEvent(event);
  };

  return (
    <Calendar
      localizer={localizer}
      events={events}
      startAccessor="start"
      endAccessor="end"
      defaultView="month"
      views={['month', 'week','day', 'agenda']}
      components={{
        agenda: {
          body: CustomAgenda,
        },
      }}
      allDayAccessor="isAllDay" // Specify the allDayAccessor property
    />
  );
};

export default MyCalendar;
