import React, { Component } from 'react';
import './Form.css'; // Import the CSS file
import { fetchColumns } from './getids';

class Form extends Component {
  // ... Rest of your component code ...

  render() {
    const { columns } = this.state;

    return (
      <div className="form-container"> {/* Apply the form container style */}
        <form onSubmit={this.handleSubmit}>
          {columns.map(column => (
            <div key={column.id} className="form-input"> {/* Apply the input style */}
              <label>{column.title}</label>
              {column.type === 'color' ? (
                <select
                  name={column.title}
                  value={this.state[column.title]}
                  onChange={this.handleInputChange}
                >
                  {/* Options for the dropdown */}
                </select>
              ) : (
                <input
                  type={column.type}
                  name={column.title}
                  value={this.state[column.title]}
                  onChange={this.handleInputChange}
                />
              )}
            </div>
          ))}
          <div className="form-submit"> {/* Apply the submit button style */}
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Form;
